function FavoriteList({favorite}){
  return(
     <>
        <div className="summary">
          <h2>Favorites</h2>
        </div>
         <ul className="list">
          {favorite.map((movie) => (
            <FavoriteMovie movie={movie} key={movie.id}/>
          ))}
        </ul>
     </>
  )
}



